const $mobile=480,$tablet=768,$desktop=1200,slider=document.querySelector(".intro__slider"),slides=document.querySelectorAll(".slider-item"),itemCount=slides.length,nextItem=document.querySelector(".next"),previousItem=document.querySelector(".previous");let animation,count=0,time=5e3;function showNextItem(){slider.classList.remove("slider--first-load"),slides[count].classList.remove("active"),count<itemCount-1?count++:count=0,clearInterval(animation),animation=setInterval(showNextItem,time),slides[count].classList.add("active")}function showPreviousItem(){slider.classList.remove("slider--first-load"),slides[count].classList.remove("active"),count>0?count--:count=itemCount-1,clearInterval(animation),animation=setInterval(showNextItem,time),slides[count].classList.add("active")}function sliderStart(){window.matchMedia("(min-width: 1200px)").matches&&(animation=window.setInterval(showNextItem,time))}nextItem.addEventListener("click",showNextItem),previousItem.addEventListener("click",showPreviousItem);